import * as Yup from 'yup';
import PropTypes from 'prop-types';
import { useSnackbar } from 'notistack5';
import { useNavigate } from 'react-router-dom';
import { Form, FormikProvider, useFormik } from 'formik';
// material
import { LoadingButton } from '@material-ui/lab';
import { Box, Card, Grid, Stack, Typography, TextareaAutosize } from '@material-ui/core';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
// utils
import axios from '../../../utils/axios';
import fakeRequest from '../../../utils/fakeRequest';

// ----------------------------------------------------------------------

export default function AnnouncementPostForm() {
  const { enqueueSnackbar } = useSnackbar();

  const itemValidation = Yup.object().shape({
    message: Yup.string().required('This field is required')
  });

  const formik = useFormik({
    enableReinitialize: true,
    initialValues: { message: '' },
    validationSchema: itemValidation,
    onSubmit: async (values, { setSubmitting, resetForm, setErrors }) => {
      try {
        await fakeRequest(500);

        await axios.put('/chat/announcementpost/', values);

        resetForm();
        setSubmitting(false);
        enqueueSnackbar('Posted announcement successful', { variant: 'success' });
      } catch (error) {
        console.error(error);
        setSubmitting(false);
        setErrors(error);
      }
    }
  });

  const { errors, values, touched, handleSubmit, isSubmitting, setFieldValue, getFieldProps } = formik;

  return (
    <FormikProvider value={formik}>
      <Form noValidate autoComplete="off" onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={12}>
            <Card sx={{ p: 3 }}>
              <Stack spacing={2}>
                <Stack direction={{ xs: 'column', sm: 'row' }} spacing={{ xs: 3, sm: 2 }}>
                  <TextareaAutosize
                    {...getFieldProps('message')}
                    maxRows={20}
                    minRows={6}
                    aria-label="Post Public Announcement"
                    placeholder="text input(max. 2000 words)"
                    style={{ width: '100%', padding: '10px' }}
                    error={Boolean(touched.message && errors.message)}
                    helperText={touched.message && errors.message}
                  />
                </Stack>

                <Stack direction={{ xs: 'column', sm: 'row' }} spacing={{ xs: 3, sm: 2 }}>
                  <Typography variant="body1" component="div" style={{ lineHeight: 1.5 }}>
                    Add pictures (max. 9) from hard-disk.
                  </Typography>
                </Stack>

                <Stack direction={{ xs: 'column', sm: 'row' }} spacing={{ xs: 10, sm: 8 }}>
                  <Typography variant="body1" component="div" style={{ lineHeight: 2 }}>
                    Post at
                  </Typography>
                  <RadioGroup
                    {...getFieldProps('posted_at')}
                    aria-label="Posted at"
                    error={Boolean(touched.posted_at && errors.posted_at)}
                    helperText={touched.posted_at && errors.posted_at}
                  >
                    <FormControlLabel value="public" control={<Radio />} label="Public chat room" />
                    <FormControlLabel value="top" control={<Radio />} label="Top column" />
                  </RadioGroup>
                </Stack>

                {/* <Stack direction={{ xs: 'column', sm: 'row' }} spacing={{ xs: 3, sm: 2 }}>
                  <AutoAwesomeSharpIcon />
                  <Typography variant="body1" component="div" style={{ lineHeight: 2 }}>
                    After the user logs into the APP, system downloads last
                  </Typography>
                  <TextField
                    {...getFieldProps('downloads_message_num')}
                    variant="standard"
                    style={{ width: 80 }}
                    error={Boolean(touched.downloads_message_num && errors.downloads_message_num)}
                    helperText={touched.downloads_message_num && errors.downloads_message_num}
                  />
                  <Typography variant="body1" component="div" style={{ lineHeight: 2 }}>
                    messages of the public chat room
                  </Typography>
                </Stack> */}

                <Box sx={{ mt: 3, display: 'flex', justifyContent: 'center' }}>
                  <LoadingButton type="submit" variant="contained" loading={isSubmitting}>
                    SEND MESSAGE
                  </LoadingButton>
                </Box>
              </Stack>
            </Card>
          </Grid>
        </Grid>
      </Form>
    </FormikProvider>
  );
}
